#método de Runge-Kutta#ordem P (metodo de euler com mais termos na equação) #  2 ordem function rungekutta  a=0;  b=0.5;  #intervalo  h=0.1;  #numero de pontos   x=[a:h:b];  n=length(x);  y(1)=1; #PVI   for i=1:n-1    k1=h*fxy(x(i),y(i));    k2=h*fxy(x(i)+h,y(i)+k1);    y(i+1)=y(i)+(k1+k2)/2;  #equação de euler  end  y  ye=exp(((x.^3)/3)-3);    #solução exata  d=abs(ye-y);  plot(x,y)  grid  hold on  plot(x,ye,'r') endfunction f=fxy(x,y)  f=(y*(x^2)) - y;   #função EDOend